using UnityEngine; using System.Collections;  public class Boss {         	private int hp = 100;          // 体力 	private int power = 25; // 攻撃力 	private int mp = 53; //魔力  	//--------------------各関数-------------------- 	// 攻撃用の関数 	public void Attack() {  		Debug.Log( this.power + "のダメージを与えた" );  	}  	// 防御用の関数 	public  void Defence(int damage) {  		Debug.Log( damage+"のダメージを受けた" ); 		// 残りhpを減らす 		this.hp -= damage; 	}  	//魔法を使う関数 	public void Magic(int mpattack) 	{ 		if(this.mp >= 5) 		{ 			this.mp -= mpattack; 			Debug.Log("魔法攻撃をした。残りMPは" + this.mp); 		}else 		{ 			Debug.Log("MPが足りないため魔法が使えない。"); 		} 	} 	//---------------------------------------- } public class Test : MonoBehaviour {  	void Start () { 		// Bossクラスの変数を宣言してインスタンスを代入 		Boss lastboss = new Boss ();  		for(int i = 0; i <11; i++) 			lastboss.Magic(5); 	}  	// Update is called once per frame 	void Update () {  	} }